<?php	require_once("basicshapeabstract.class.php");	class Ellipse extends BasicShapeAbstract{				function __construct($itemId){			$this->id = $itemId;			$this->shapeType = "ellipse";		}				public function setCoordinates($cx, $cy){			$this->centerX = $cx;			$this->centerY = $cy;		}				public function setRadius($radiusX, $radiusY){			$this->radiusX = $radiusX;			$this->radiusY = $radiusY;		}			}?>