<?php	require_once("basicshapeabstract.class.php");	class Polygon extends BasicShapeAbstract{				function __construct($itemId){			$this->id = $itemId;			$this->shapeType = "polygon";		}				public function setPoints($points){						$pointsString = "";						if(sizeof($points)){							foreach($points AS $key=>$point){					$pointsString .= $point[0].",".$point[1]." ";				}						}						$this->points = $pointsString;					}			}?>