<?php	require_once("basicshapeabstract.class.php");	class Polyline extends BasicShapeAbstract{				function __construct($itemId){			$this->id = $itemId;			$this->shapeType = "polyline";		}				public function setPoints($points){						$pointsString = "";						if(sizeof($points)){							foreach($points AS $key=>$point){					$pointsString .= $point[0].",".$point[1]." ";				}						}						$this->points = $pointsString;					}			}?>