<?php	require_once("basicshapeabstract.class.php");	class Text extends BasicShapeAbstract{				function __construct($itemId){			$this->id = $itemId;			$this->shapeType = "text";		}				public function addText($text){			$this->components[] = $text;		}				public function setCoordinates($x, $y){			$this->x = $x;			$this->y = $y;		}				public function setTextFont($fontSize, $textLength, $textAnchor = "middle"){			$this->fontSize = $fontSize;			$this->textLength = $textLength;		}				public function setRotation($rotationAngles){					$rotation = "";						if(sizeof($rotationAngles)){				foreach($rotationAngles AS $key=>$value){					$rotation .= $value.", ";				}			}						$this->rotate = $rotation;				}			}?>